const initialstate={list:[]
};
const reducers=(state=initialstate, action)=>{
switch(action.type){
    case 'ADD': const [id,data]= action.payload;
    return{
        ...state,list:[
            ...state.list,{id:id,data:data}
        ]
    }
    default: return state;
    
}


}
export default reducers;
